import { fetchNums, fetchArticles } from "../store/api";
import { createSlice } from "@reduxjs/toolkit";
const initialState = { list: 0, json };
const url = "https://hacker-news.firebaseio.com/v0/topstories.json";
(async () => {
  const data = await fetchNums(url);
  const objArr = await fetchArticles(data, 5);
  console.log(objArr);
  initialState.json = objArr;
})();

const topSlice = createSlice({
  name: "top",
  initialState,
  reducers: {},
});

export const topActions = topSlice.actions;

export default topSlice;
