// Import Swiper React components
import { useSelector } from "react-redux";
import { Swiper, SwiperSlide } from "swiper/react";
import RankContents from "./RankContents";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

// import required modules
import { Pagination } from "swiper";

const url = "https://hacker-news.firebaseio.com/v0/topstories.json";
let json = async () => {
  await fetch(url)
    .then(function (response) {
      if (response.ok) {
        return response.json();
      }
      throw new Error("Network response was not ok.");
    })
    .then(function (data) {
      // data 사용하기
      return data;
    })
    .catch(function (error) {
      console.log(
        "There has been a problem with your fetch operation: ",
        error.message
      );
    });
};

console.log(json());
const Slider = () => {
  const state = useSelector((state) => state.top.json);
  console.log(state);
  return (
    <Swiper
      pagination={{ clickable: true }}
      modules={[Pagination]}
      className="mySwiper"
    >
      <SwiperSlide>
        <RankContents ranking={1} title={"harrypotter"} writer={"keyeong"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={2} title={"harrtter"} writer={"keuyeo"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={3} title={"hatter"} writer={"keneong"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={4} title={"harrypoter"} writer={"keuneong"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={5} title={"hattr"} writer={"keunyeng"} />
      </SwiperSlide>
    </Swiper>
  );
};

export default Slider;
