import styled from "styled-components";
import GlobalStyle from "./Global";
import PhoneTop from "./components/PhoneTop";
import "./styles.css";
import Application from "./components/Application";
import { fetchNums, fetchArticles } from "./store/api";
import { useDispatch } from "react-redux";
import { topActions } from "./store/top-slice";

const Phone = styled.div`
  display: flex;
  flex-direction: column;
  /* overflow: scroll; */
`;

export default function App() {
  const dispatch = useDispatch();
  const url = "https://hacker-news.firebaseio.com/v0/topstories.json";

  (async () => {
    const data = await fetchNums(url);
    const objArr = await fetchArticles(data, 5);
    dispatch(topActions.addJson(objArr));
  })();
  return (
    <>
      <GlobalStyle />
      <Phone className="App">
        <PhoneTop />
        <Application />
      </Phone>
    </>
  );
}
