// console.log(json());
const Slider = () => {
  const [state, setState] = useState([]);
  const url = "https://hacker-news.firebaseio.com/v0/topstories.json";
  const dataFetch = async (url) => {
    const res = await fetch(url);
    const data = await res.json();
    return data;
  };
  async function fetchArticles(nums) {
    const objArr = await Promise.all(
      nums.map((articleNum) => {
        return articleNum;
      })
    );
    return objArr;
  }
  useEffect(() => {
    console.log(fetchArticles(dataFetch(url)));
  }, []);
  // console.log(state);
  return (
    <Swiper
      pagination={{ clickable: true }}
      modules={[Pagination]}
      className="mySwiper"
    >
      <SwiperSlide>
        <RankContents ranking={1} title={"harrypotter"} writer={"keyeong"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={2} title={"harrtter"} writer={"keuyeo"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={3} title={"hatter"} writer={"keneong"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={4} title={"harrypoter"} writer={"keuneong"} />
      </SwiperSlide>
      <SwiperSlide>
        <RankContents ranking={5} title={"hattr"} writer={"keunyeng"} />
      </SwiperSlide>
    </Swiper>
  );
};

export default Slider;
